mapper.insert:
INSERT INTO liquid_schema_cache (
    theme_name, version, file_path, section_name, section_type,
    schema_json, settings_count, settings_hash
) VALUES (
    #{themeName}, #{version}, #{filePath}, #{sectionName}, #{sectionType},
    #{schemaJson}, #{settingsCount}, #{settingsHash}
)

INSERT INTO liquid_schema_cache (
    theme_name, version, file_path, section_name, section_type,
    schema_json, settings_count, settings_hash
) VALUES

    (
        #{item.themeName}, #{item.version}, #{item.filePath},
        #{item.sectionName}, #{item.sectionType}, #{item.schemaJson},
        #{item.settingsCount}, #{item.settingsHash}
    )

mapper.select:
SELECT * FROM liquid_schema_cache
WHERE theme_name = #{themeName}
  AND version = #{version}
ORDER BY file_path

SELECT * FROM liquid_schema_cache
WHERE theme_name = #{themeName}
  AND version = #{version}
  AND file_path = #{filePath}
LIMIT 1

SELECT * FROM liquid_schema_cache
WHERE theme_name = #{themeName}
  AND version = #{version}
  AND section_name = #{sectionName}

mapper.delete:
DELETE FROM liquid_schema_cache
WHERE theme_name = #{themeName}
  AND version = #{version}
