mapper.update:
UPDATE products
handle = #{handle}, title = #{title}, body_html = #{bodyHtml}, vendor = #{vendor}, tags = #{tags}, published = #{published}, product_url = #{productUrl},
WHERE id = #{id}

mapper.select:
SELECT DISTINCT p.*, (SELECT MAX(publish_status) FROM product_shops WHERE product_id = p.id) as publishStatus, (SELECT MAX(last_export_time) FROM product_shops WHERE product_id = p.id) as lastExportTime, (SELECT v.image_url FROM product_variants v WHERE v.product_id = p.id AND v.image_url IS NOT NULL LIMIT 1) as imageUrl FROM products p
LEFT JOIN product_shops ps ON p.id = ps.product_id
AND p.title LIKE CONCAT('%', #{title}, '%')
AND p.tags LIKE CONCAT('%', #{tags}, '%')
AND p.published = #{published}
AND ps.shop_id = #{shopId}
ORDER BY p.created_at DESC LIMIT #{offset}, #{limit}

SELECT COUNT(DISTINCT p.id) FROM products p
LEFT JOIN product_shops ps ON p.id = ps.product_id
AND p.title LIKE CONCAT('%', #{title}, '%')
AND p.tags LIKE CONCAT('%', #{tags}, '%')
AND p.published = #{published}
AND ps.shop_id = #{shopId}
