mapper.insert:
INSERT INTO product_visibility (
    product_id, user_id, role_id, shop_id, granted_by, granted_at, expires_at
) VALUES (
    #{productId}, #{userId}, #{roleId}, #{shopId}, #{grantedBy}, #{grantedAt}, #{expiresAt}
)

mapper.delete:
DELETE FROM product_visibility 
WHERE product_id = #{productId} 
  AND user_id = #{userId}

DELETE FROM product_visibility 
WHERE product_id = #{productId} 
  AND role_id = #{roleId}

DELETE FROM product_visibility 
WHERE product_id IN

    #{productId}

mapper.select:
SELECT COUNT(*)
FROM product_visibility
WHERE user_id = #{userId}
  AND product_id = #{productId}

      AND (shop_id IS NULL OR shop_id = #{shopId})

  AND (expires_at IS NULL OR expires_at > NOW())

SELECT COUNT(*) FROM product_visibility WHERE role_id IN
#{roleId}
AND product_id = #{productId}
AND (shop_id IS NULL OR shop_id = #{shopId})
AND (expires_at IS NULL OR expires_at > NOW())

SELECT DISTINCT p.*, (SELECT v.image_url FROM product_variants v WHERE v.product_id = p.id AND v.image_url IS NOT NULL LIMIT 1) as imageUrl FROM products p INNER JOIN product_visibility pv ON p.id = pv.product_id WHERE ( (pv.user_id = #{userId}) OR (pv.role_id IN
#{roleId}
) )
AND (pv.shop_id IS NULL OR pv.shop_id = #{shopId})
AND (pv.expires_at IS NULL OR pv.expires_at > NOW())
AND ( p.title LIKE CONCAT('%', #{keyword}, '%') OR p.handle LIKE CONCAT('%', #{keyword}, '%') OR p.vendor LIKE CONCAT('%', #{keyword}, '%') )
ORDER BY p.id DESC
LIMIT #{offset}, #{limit}

SELECT COUNT(DISTINCT p.id) FROM products p INNER JOIN product_visibility pv ON p.id = pv.product_id WHERE ( (pv.user_id = #{userId}) OR (pv.role_id IN
#{roleId}
) )
AND (pv.shop_id IS NULL OR pv.shop_id = #{shopId})
AND (pv.expires_at IS NULL OR pv.expires_at > NOW())
AND ( p.title LIKE CONCAT('%', #{keyword}, '%') OR p.handle LIKE CONCAT('%', #{keyword}, '%') OR p.vendor LIKE CONCAT('%', #{keyword}, '%') )
