mapper.insert:
INSERT INTO users (
    username, password, email, phone, real_name, role, status, avatar,
    ding_union_id, ding_user_id, corp_id, login_source
) VALUES (
    #{username}, #{password}, #{email}, #{phone}, #{realName}, #{role}, #{status}, #{avatar},
    #{dingUnionId}, #{dingUserId}, #{corpId}, #{loginSource}
)

mapper.update:
UPDATE users
email = #{email}, phone = #{phone}, real_name = #{realName}, avatar = #{avatar}, role = #{role}, status = #{status},
WHERE id = #{id}

UPDATE users
SET password = #{password}
WHERE id = #{id}

UPDATE users
SET last_login_time = #{lastLoginTime},
    last_login_ip = #{lastLoginIp}
WHERE id = #{id}

UPDATE users
SET status = #{status}
WHERE id = #{id}

mapper.insert:
INSERT INTO users (
    username, password, email, phone, real_name, role, status, avatar,
    ding_union_id, ding_user_id, corp_id, login_source, sync_enabled, title, job_number
) VALUES

    (
    #{user.username}, #{user.password}, #{user.email}, #{user.phone}, #{user.realName},
    #{user.role}, #{user.status}, #{user.avatar}, #{user.dingUnionId}, #{user.dingUserId},
    #{user.corpId}, #{user.loginSource}, #{user.syncEnabled}, #{user.title}, #{user.jobNumber}
    )

update.foreach:
UPDATE users
username = #{user.username}, email = #{user.email}, phone = #{user.phone}, real_name = #{user.realName}, avatar = #{user.avatar}, status = #{user.status}, ding_union_id = #{user.dingUnionId}, ding_user_id = #{user.dingUserId}, title = #{user.title}, job_number = #{user.jobNumber}
WHERE id = #{user.id}
